import React, { useEffect, useRef } from 'react';
import '@arcgis/core/assets/esri/themes/light/main.css';
import { loadModules } from 'esri-loader';

const MapComponent = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    let view;

    loadModules(['esri/Map', 'esri/views/MapView', 'esri/layers/FeatureLayer'])
      .then(([Map, MapView, FeatureLayer]) => {
        const map = new Map({
          basemap: 'streets'
        });

        view = new MapView({
          container: mapRef.current,
          map: map,
          center: [34.8516, 31.0461], // ישראל
          zoom: 7
        });

        const featureLayer = new FeatureLayer({
          url: 'https://services.arcgis.com/P3ePLMYs2RVChkJx/arcgis/rest/services/World_Cities/FeatureServer/0',
          outFields: ['CITY_NAME', 'POP', 'CAPITAL'],
          definitionExpression: "CAPITAL = 'Yes'", // מציג רק ערי בירה
          popupTemplate: {
            title: "{CITY_NAME} - Population: {POP}",
            content: `
              <div style="font-family: Arial, sans-serif; padding: 8px;">
                <p style="font-size: 14px; margin: 5px 0;">
                  <strong>🌍 City:</strong> {CITY_NAME}
                </p>
                <p style="font-size: 14px; margin: 5px 0;">
                  <strong>👥 Population:</strong> {POP.toLocaleString()}
                </p>
              </div>
            `
          }
        });

        map.add(featureLayer);
      })
      .catch((err) => console.error(err));

    return () => view?.destroy();
  }, []);

  return <div style={{ width: '100%', height: '100vh' }} ref={mapRef}></div>;
};

export default MapComponent;
